import { useState, useEffect } from 'react';
import axios from 'axios';

export default function TaskForm({ existing, onDone }) {
  const [form, setForm] = useState({ title:'', description:'', status:'todo', dueDate:'' });
  const api = process.env.REACT_APP_API_URL;

  useEffect(() => { if (existing) setForm({
    title: existing.title,
    description: existing.description || '',
    status: existing.status,
    dueDate: existing.dueDate ? existing.dueDate.split('T')[0] : ''
  }); }, [existing]);

  const submit = e => {
    e.preventDefault();
    const fn = existing ? axios.put : axios.post;
    const url = existing
      ? `${api}/tasks/${existing._id}`
      : `${api}/tasks`;
    fn(url, form).then(() => onDone());
  };

  return (
    <form onSubmit={submit} style={{ margin:'16px 0' }}>
      <input
        required placeholder="Title"
        value={form.title}
        onChange={e => setForm({...form, title:e.target.value})}
      /><br/>
      <textarea
        placeholder="Description"
        value={form.description}
        onChange={e => setForm({...form, description:e.target.value})}
      /><br/>
      <select
        value={form.status}
        onChange={e => setForm({...form, status:e.target.value})}
      >
        <option value="todo">To Do</option>
        <option value="in_progress">In Progress</option>
        <option value="done">Done</option>
      </select><br/>
      <input
        type="date"
        value={form.dueDate}
        onChange={e => setForm({...form, dueDate:e.target.value})}
      /><br/>
      <button type="submit">{existing ? 'Update' : 'Add'} Task</button>
    </form>
  );
}
