import { useEffect, useState } from 'react';
import axios from 'axios';

export default function TaskList({ onEdit }) {
  const [tasks, setTasks] = useState([]);
  const api = process.env.REACT_APP_API_URL;

  useEffect(() => {
    axios.get(`${api}/tasks`).then(r => setTasks(r.data));
  }, []);

  const del = id => {
    axios.delete(`${api}/tasks/${id}`)
      .then(() => setTasks(tasks.filter(t => t._id !== id)));
  };

  return (
    <div>
      {tasks.map(t => (
        <div key={t._id} style={{ border:'1px solid #ccc', padding:'8px', margin:'8px 0' }}>
          <h4>{t.title} ({t.status})</h4>
          {t.dueDate && <small>Due: {new Date(t.dueDate).toLocaleDateString()}</small>}
          <button onClick={() => onEdit(t)}>Edit</button>
          <button onClick={() => del(t._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}
